import { useCallback, useState } from "react";
import { Transaction } from "../utils/types";
import { TransactionsByEmployeeResult } from "./types";
import { useCustomFetch } from "./useCustomFetch";

export function useTransactionsByEmployee(): TransactionsByEmployeeResult {
  const { fetchWithCache, loading } = useCustomFetch();
  const [transactions, setTransactions] = useState<Transaction[] | null>(null);
  const [hasMore, setHasMore] = useState(true);

  const fetchById = useCallback(async (employeeId: string) => {
    const response = await fetchWithCache<Transaction[], { employeeId: string }>("transactionsByEmployee", {
      employeeId,
    });

    setTransactions((prevTransactions) => {
      if (response === null) {
        setHasMore(false);
        return prevTransactions;
      }

      const combinedData = [...(prevTransactions || []), ...response];

      if (response.length === 0) {
        setHasMore(false); // No more transactions to load
      }

      return combinedData;
    });
  }, [fetchWithCache]);

  const invalidateData = useCallback(() => {
    setTransactions(null);
    setHasMore(true);
  }, []);

  return { data: transactions, loading, fetchById, invalidateData, hasMore };
}
